    }
    body {
      font-family: 'Inter', 'Segoe UI', -apple-system, BlinkMacSystemFont, Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
      font-size: var(--base-font-size);
      margin: 0;
      padding: 0;
      background-color: #f5f7fa;
    }
    .container {
      max-width: 800px;
      margin: 0 auto;
      padding: var(--spacing-base);
      width: 100%;
      box-sizing: border-box;
      background-color: white;
    }
    .section, .card, .stock-card, .indices-table, .sector-table, .futures-table, .volatility-table, .events-table, .yields-table {
      max-width: 100%;
      width: 100%;
      box-sizing: border-box;
      padding: var(--spacing-base);
      margin-bottom: var(--spacing-base);
    }
    .label-col {
      flex: 0 1 auto;
      min-width: 0;
      font-weight: bold;
    }
    .row, .flex-row {
      display: flex;
      flex-direction: row;
      gap: var(--spacing-base);
    }
    .stock-cards-container {
      display: flex;
      flex-wrap: wrap;
      gap: var(--spacing-base);
    }
    .stock-card {
      flex: 1 1 48%;
      min-width: 260px;
      max-width: 48%;
    }
    .decision-banner {
      font-size: 1rem;
    }
    .card-title, .title {
      font-size: 1.2rem;
    }
    .small-text {
      font-size: 0.85rem;
    }
